SELECT REGION.R_COMMENT, SUPPLIER.S_NAME, PARTSUPP.PS_SUPPKEY, NATION.N_COMMENT FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE SUPPLIER.S_ADDRESS <= '89EJ5KSX3IMXJQBVXOBC,' ORDER BY PARTSUPP.PS_SUPPKEY DESC, REGION.R_COMMENT DESC;
