SELECT CUSTOMER.C_MKTSEGMENT, LINEITEM.L_RECEIPTDATE, ORDERS.O_ORDERDATE FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE CUSTOMER.C_NAME < 'CUSTOMER#000000136' ORDER BY ORDERS.O_ORDERDATE DESC, LINEITEM.L_RECEIPTDATE ASC;
