SELECT PART.P_PARTKEY, SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPLYCOST, AVG(PARTSUPP.PS_SUPPKEY) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE SUPPLIER.S_ADDRESS >= '1KHUGZEGWM3UA7DSYMEKYBSK' AND SUPPLIER.S_NATIONKEY < 14 GROUP BY PART.P_PARTKEY, SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPLYCOST;
