SELECT LINEITEM.L_PARTKEY, ORDERS.O_ORDERSTATUS, PARTSUPP.PS_COMMENT, PART.P_RETAILPRICE, AVG(PARTSUPP.PS_SUPPKEY) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY LINEITEM.L_PARTKEY, ORDERS.O_ORDERSTATUS, PARTSUPP.PS_COMMENT, PART.P_RETAILPRICE ORDER BY AVG(PARTSUPP.PS_SUPPKEY) DESC;
