SELECT LINEITEM.L_ORDERKEY, ORDERS.O_TOTALPRICE, PARTSUPP.PS_SUPPKEY, COUNT(LINEITEM.L_SHIPDATE) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY LINEITEM.L_ORDERKEY, ORDERS.O_TOTALPRICE, PARTSUPP.PS_SUPPKEY ORDER BY COUNT(LINEITEM.L_SHIPDATE) DESC;
