SELECT CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_PHONE, NATION.N_REGIONKEY, MIN(CUSTOMER.C_ADDRESS), MIN(SUPPLIER.S_PHONE) FROM CUSTOMER, NATION, SUPPLIER GROUP BY CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_PHONE, NATION.N_REGIONKEY ORDER BY NATION.N_REGIONKEY ASC, CUSTOMER.C_MKTSEGMENT DESC, SUPPLIER.S_PHONE DESC;
