SELECT REGION.R_REGIONKEY, PART.P_COMMENT, PARTSUPP.PS_COMMENT, SUPPLIER.S_ACCTBAL, NATION.N_REGIONKEY FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY;
