SELECT PART.P_COMMENT, LINEITEM.L_COMMITDATE, ORDERS.O_COMMENT, CUSTOMER.C_NATIONKEY, PARTSUPP.PS_SUPPKEY, COUNT(PART.P_COMMENT) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY PART.P_COMMENT, LINEITEM.L_COMMITDATE, ORDERS.O_COMMENT, CUSTOMER.C_NATIONKEY, PARTSUPP.PS_SUPPKEY HAVING COUNT(PART.P_COMMENT) = 6;
