SELECT NATION.N_NAME, ORDERS.O_TOTALPRICE, REGION.R_COMMENT, CUSTOMER.C_ADDRESS, COUNT(CUSTOMER.C_MKTSEGMENT) FROM ORDERS, CUSTOMER, NATION, REGION WHERE CUSTOMER.C_ADDRESS <= 'O8AIBCCRKXVQFH8HF,7O' GROUP BY NATION.N_NAME, ORDERS.O_TOTALPRICE, REGION.R_COMMENT, CUSTOMER.C_ADDRESS HAVING COUNT(CUSTOMER.C_MKTSEGMENT) >= 5 ORDER BY COUNT(CUSTOMER.C_MKTSEGMENT) ASC;
