SELECT SUPPLIER.S_ADDRESS, LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_SUPPKEY, MAX(SUPPLIER.S_ADDRESS) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY SUPPLIER.S_ADDRESS, LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_SUPPKEY HAVING MAX(SUPPLIER.S_ADDRESS) = 'TQXUVM7S7CNK';
