SELECT ORDERS.O_TOTALPRICE, CUSTOMER.C_ADDRESS, MIN(ORDERS.O_CLERK), MAX(ORDERS.O_COMMENT), MIN(ORDERS.O_ORDERSTATUS) FROM CUSTOMER, ORDERS GROUP BY ORDERS.O_TOTALPRICE, CUSTOMER.C_ADDRESS HAVING MIN(ORDERS.O_CLERK) <= 'CLERK#000000344' AND MAX(ORDERS.O_COMMENT) > 'FINAL THEODOLITES ALONGSIDE OF THE CAREFU' ORDER BY ORDERS.O_TOTALPRICE DESC, CUSTOMER.C_ADDRESS DESC;
