SELECT SUPPLIER.S_COMMENT, PARTSUPP.PS_AVAILQTY, PART.P_MFGR FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE PART.P_TYPE != 'STANDARD BRUSHED NICKEL' AND PART.P_MFGR < 'MANUFACTURER#3           ' ORDER BY SUPPLIER.S_COMMENT DESC, PARTSUPP.PS_AVAILQTY DESC, PART.P_MFGR ASC;
