SELECT ORDERS.O_CUSTKEY, CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY, AVG(CUSTOMER.C_NATIONKEY) FROM ORDERS, CUSTOMER, NATION, SUPPLIER GROUP BY ORDERS.O_CUSTKEY, CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY HAVING AVG(CUSTOMER.C_NATIONKEY) < 9 ORDER BY AVG(CUSTOMER.C_NATIONKEY) ASC;
