SELECT NATION.N_NAME, ORDERS.O_SHIPPRIORITY, REGION.R_COMMENT, CUSTOMER.C_COMMENT FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY WHERE ORDERS.O_CLERK < 'CLERK#000000195' ORDER BY REGION.R_COMMENT ASC, NATION.N_NAME ASC, CUSTOMER.C_COMMENT ASC;
