SELECT CUSTOMER.C_NATIONKEY, NATION.N_COMMENT FROM NATION JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE NATION.N_REGIONKEY >= 63135.96 AND CUSTOMER.C_ACCTBAL = 2912.00 AND CUSTOMER.C_COMMENT != 'ONIC ACCOUNTS. FLUFFILY SILENT PINTO BEANS BOOST BLITHELY ACCORDING TO THE FLUFFILY EXP' ORDER BY NATION.N_COMMENT ASC;
