SELECT ORDERS.O_SHIPPRIORITY, REGION.R_COMMENT, CUSTOMER.C_CUSTKEY, NATION.N_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY CUSTOMER.C_CUSTKEY DESC, REGION.R_COMMENT DESC, NATION.N_REGIONKEY ASC, ORDERS.O_SHIPPRIORITY ASC;
