SELECT LINEITEM.L_RECEIPTDATE, NATION.N_NATIONKEY, ORDERS.O_CUSTKEY, CUSTOMER.C_PHONE, PARTSUPP.PS_COMMENT, SUPPLIER.S_ADDRESS FROM SUPPLIER, NATION, CUSTOMER, ORDERS, LINEITEM, PARTSUPP WHERE NATION.N_REGIONKEY <= 12675 ORDER BY LINEITEM.L_RECEIPTDATE DESC, SUPPLIER.S_ADDRESS ASC, NATION.N_NATIONKEY ASC, CUSTOMER.C_PHONE ASC, ORDERS.O_CUSTKEY DESC;
