SELECT PART.P_NAME, NATION.N_COMMENT, PARTSUPP.PS_SUPPKEY, SUPPLIER.S_ADDRESS, REGION.R_REGIONKEY FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE PARTSUPP.PS_PARTKEY = 38 ORDER BY NATION.N_COMMENT ASC;
