SELECT REGION.R_REGIONKEY, NATION.N_NAME, SUPPLIER.S_SUPPKEY, AVG(NATION.N_REGIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY GROUP BY REGION.R_REGIONKEY, NATION.N_NAME, SUPPLIER.S_SUPPKEY ORDER BY NATION.N_NAME DESC, REGION.R_REGIONKEY DESC, SUPPLIER.S_SUPPKEY DESC;
