SELECT SUPPLIER.S_PHONE, NATION.N_NAME, CUSTOMER.C_ACCTBAL, MAX(CUSTOMER.C_COMMENT) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY GROUP BY SUPPLIER.S_PHONE, NATION.N_NAME, CUSTOMER.C_ACCTBAL ORDER BY NATION.N_NAME ASC, CUSTOMER.C_ACCTBAL ASC, SUPPLIER.S_PHONE ASC;
