SELECT PARTSUPP.PS_AVAILQTY, REGION.R_REGIONKEY, SUPPLIER.S_NATIONKEY, NATION.N_COMMENT FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE SUPPLIER.S_NAME != 'SUPPLIER#000000007       ' ORDER BY NATION.N_COMMENT DESC, PARTSUPP.PS_AVAILQTY ASC, REGION.R_REGIONKEY ASC;
