SELECT PART.P_PARTKEY, ORDERS.O_SHIPPRIORITY, LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_AVAILQTY, CUSTOMER.C_PHONE, MAX(LINEITEM.L_QUANTITY) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE LINEITEM.L_PARTKEY >= 14249 AND ORDERS.O_TOTALPRICE = 121746.70 GROUP BY PART.P_PARTKEY, ORDERS.O_SHIPPRIORITY, LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_AVAILQTY, CUSTOMER.C_PHONE ORDER BY ORDERS.O_SHIPPRIORITY ASC, PART.P_PARTKEY ASC;
