SELECT LINEITEM.L_DISCOUNT, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_SUPPLYCOST, NATION.N_NAME FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY WHERE LINEITEM.L_COMMITDATE > '1994-05-24' AND PARTSUPP.PS_PARTKEY != 104 ORDER BY LINEITEM.L_DISCOUNT ASC, PARTSUPP.PS_SUPPLYCOST DESC, NATION.N_NAME DESC, SUPPLIER.S_ACCTBAL DESC;
