SELECT PARTSUPP.PS_SUPPKEY, LINEITEM.L_SHIPDATE, CUSTOMER.C_PHONE, ORDERS.O_ORDERPRIORITY, MIN(CUSTOMER.C_PHONE) FROM PARTSUPP, LINEITEM, ORDERS, CUSTOMER WHERE LINEITEM.L_LINENUMBER < 3 GROUP BY PARTSUPP.PS_SUPPKEY, LINEITEM.L_SHIPDATE, CUSTOMER.C_PHONE, ORDERS.O_ORDERPRIORITY ORDER BY ORDERS.O_ORDERPRIORITY ASC, PARTSUPP.PS_SUPPKEY DESC, LINEITEM.L_SHIPDATE ASC, CUSTOMER.C_PHONE ASC;
