SELECT CUSTOMER.C_ACCTBAL, ORDERS.O_CUSTKEY, LINEITEM.L_LINENUMBER, PARTSUPP.PS_COMMENT, NATION.N_REGIONKEY, REGION.R_REGIONKEY FROM PARTSUPP, LINEITEM, ORDERS, CUSTOMER, NATION, REGION WHERE LINEITEM.L_TAX > 0.00 ORDER BY LINEITEM.L_LINENUMBER DESC, CUSTOMER.C_ACCTBAL ASC, NATION.N_REGIONKEY ASC, PARTSUPP.PS_COMMENT ASC, ORDERS.O_CUSTKEY DESC, REGION.R_REGIONKEY ASC;
