SELECT LINEITEM.L_QUANTITY, PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_PHONE, ORDERS.O_SHIPPRIORITY, CUSTOMER.C_MKTSEGMENT, NATION.N_REGIONKEY FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY;
