SELECT SUPPLIER.S_ADDRESS, NATION.N_REGIONKEY, REGION.R_REGIONKEY FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE SUPPLIER.S_PHONE < '34-852-489-8585' ORDER BY NATION.N_REGIONKEY ASC, REGION.R_REGIONKEY DESC, SUPPLIER.S_ADDRESS DESC;
