SELECT ORDERS.O_CLERK, LINEITEM.L_DISCOUNT, COUNT(LINEITEM.L_DISCOUNT) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY WHERE ORDERS.O_TOTALPRICE >= 291170.71 AND LINEITEM.L_COMMITDATE < '1996-05-15' GROUP BY ORDERS.O_CLERK, LINEITEM.L_DISCOUNT HAVING COUNT(LINEITEM.L_DISCOUNT) <= 1;
