SELECT ORDERS.O_TOTALPRICE, PART.P_SIZE, LINEITEM.L_TAX, PARTSUPP.PS_PARTKEY FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE ORDERS.O_SHIPPRIORITY < 0 AND ORDERS.O_ORDERDATE != '1993-09-07' ORDER BY PART.P_SIZE ASC, LINEITEM.L_TAX DESC, PARTSUPP.PS_PARTKEY DESC;
