SELECT SUPPLIER.S_PHONE, ORDERS.O_CLERK, LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY WHERE ORDERS.O_ORDERDATE >= '1997-12-02' ORDER BY PARTSUPP.PS_SUPPLYCOST ASC;
