SELECT REGION.R_NAME, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY, NATION.N_COMMENT FROM REGION, NATION, SUPPLIER, PARTSUPP WHERE PARTSUPP.PS_COMMENT != 'LY DOGGED, REGULAR DEPENDENCIES. EXPRESS, EVEN PACKAGES ARE ' ORDER BY SUPPLIER.S_NATIONKEY ASC, NATION.N_COMMENT ASC, REGION.R_NAME DESC, PARTSUPP.PS_AVAILQTY ASC;
