SELECT LINEITEM.L_LINENUMBER, ORDERS.O_CLERK, SUM(LINEITEM.L_TAX) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY WHERE LINEITEM.L_RETURNFLAG > 'N' AND ORDERS.O_COMMENT != 'LE SLYLY ABOUT THE SLYLY FINAL DOLPHINS. FU' GROUP BY LINEITEM.L_LINENUMBER, ORDERS.O_CLERK ORDER BY SUM(LINEITEM.L_TAX) ASC;
