SELECT CUSTOMER.C_NAME, ORDERS.O_ORDERDATE, SUPPLIER.S_SUPPKEY, NATION.N_NATIONKEY, MIN(NATION.N_REGIONKEY) FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_CUSTKEY < 30 GROUP BY CUSTOMER.C_NAME, ORDERS.O_ORDERDATE, SUPPLIER.S_SUPPKEY, NATION.N_NATIONKEY ORDER BY MIN(NATION.N_REGIONKEY) DESC;
