SELECT NATION.N_COMMENT, REGION.R_NAME FROM REGION, NATION WHERE REGION.R_COMMENT >= '4678' AND NATION.N_NAME != 'OLITES. SILENT, SPECIAL DEPOSITS EAT SLYLY QUICKLY EXPRESS PACKAGES; HOCKEY P' AND NATION.N_NATIONKEY < 2145 AND REGION.R_REGIONKEY > 5515 AND REGION.R_REGIONKEY <= 8738 ORDER BY NATION.N_COMMENT ASC;
