SELECT ORDERS.O_CUSTKEY, CUSTOMER.C_PHONE, NATION.N_COMMENT, REGION.R_NAME, SUM(NATION.N_REGIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY GROUP BY ORDERS.O_CUSTKEY, CUSTOMER.C_PHONE, NATION.N_COMMENT, REGION.R_NAME;
