SELECT ORDERS.O_COMMENT, CUSTOMER.C_NAME, NATION.N_NATIONKEY, COUNT(ORDERS.O_ORDERDATE), SUM(ORDERS.O_CUSTKEY) FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY GROUP BY ORDERS.O_COMMENT, CUSTOMER.C_NAME, NATION.N_NATIONKEY ORDER BY ORDERS.O_COMMENT ASC;
