SELECT PART.P_RETAILPRICE, PARTSUPP.PS_SUPPKEY, MIN(PARTSUPP.PS_SUPPLYCOST) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY WHERE PARTSUPP.PS_COMMENT > 'S. BLITHELY EXPRESS THEODOLITES SLEEP BLITHELY ALONGSIDE OF THE REQUESTS?' AND PARTSUPP.PS_COMMENT > 'LY ACCORDING TO THE IRONIC REQUESTS-- SLYLY FINAL ACCOUNTS PRINT CAREFULLY DEPTHS? PENDING, UNUSUAL ACCOUNTS SOLVE ' GROUP BY PART.P_RETAILPRICE, PARTSUPP.PS_SUPPKEY ORDER BY PART.P_RETAILPRICE DESC, PARTSUPP.PS_SUPPKEY ASC;
