SELECT LINEITEM.L_SUPPKEY, ORDERS.O_CLERK, PARTSUPP.PS_PARTKEY, MAX(PARTSUPP.PS_SUPPLYCOST) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY GROUP BY LINEITEM.L_SUPPKEY, ORDERS.O_CLERK, PARTSUPP.PS_PARTKEY HAVING MAX(PARTSUPP.PS_SUPPLYCOST) < 521.06 ORDER BY MAX(PARTSUPP.PS_SUPPLYCOST) ASC;
