SELECT REGION.R_NAME, MIN(REGION.R_NAME), COUNT(REGION.R_REGIONKEY), MAX(REGION.R_COMMENT) FROM REGION WHERE REGION.R_REGIONKEY != 30975.21 GROUP BY REGION.R_NAME HAVING MIN(REGION.R_NAME) >= 'NATION' AND MAX(REGION.R_COMMENT) = 'CUSTOMER' ORDER BY COUNT(REGION.R_REGIONKEY) ASC, MAX(REGION.R_COMMENT) ASC, MIN(REGION.R_NAME) DESC;
