SELECT CUSTOMER.C_CUSTKEY, PARTSUPP.PS_PARTKEY, NATION.N_REGIONKEY, SUPPLIER.S_ADDRESS, COUNT(CUSTOMER.C_ACCTBAL), COUNT(NATION.N_NATIONKEY) FROM PARTSUPP, SUPPLIER, NATION, CUSTOMER GROUP BY CUSTOMER.C_CUSTKEY, PARTSUPP.PS_PARTKEY, NATION.N_REGIONKEY, SUPPLIER.S_ADDRESS ORDER BY NATION.N_REGIONKEY DESC, SUPPLIER.S_ADDRESS ASC;
