SELECT NATION.N_REGIONKEY, CUSTOMER.C_ADDRESS, SUPPLIER.S_COMMENT, COUNT(SUPPLIER.S_NAME) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_NAME >= 'CUSTOMER#000000041' AND SUPPLIER.S_NATIONKEY >= 11 GROUP BY NATION.N_REGIONKEY, CUSTOMER.C_ADDRESS, SUPPLIER.S_COMMENT ORDER BY CUSTOMER.C_ADDRESS DESC, NATION.N_REGIONKEY DESC, SUPPLIER.S_COMMENT DESC;
