SELECT ORDERS.O_ORDERSTATUS, CUSTOMER.C_ADDRESS, NATION.N_NATIONKEY, MIN(CUSTOMER.C_CUSTKEY) FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY GROUP BY ORDERS.O_ORDERSTATUS, CUSTOMER.C_ADDRESS, NATION.N_NATIONKEY ORDER BY NATION.N_NATIONKEY ASC;
