SELECT NATION.N_COMMENT, SUPPLIER.S_ACCTBAL, PART.P_SIZE, PARTSUPP.PS_SUPPLYCOST FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY ORDER BY NATION.N_COMMENT ASC, PART.P_SIZE ASC;
