SELECT LINEITEM.L_COMMITDATE, ORDERS.O_SHIPPRIORITY, PARTSUPP.PS_SUPPKEY, MIN(PARTSUPP.PS_AVAILQTY) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY LINEITEM.L_COMMITDATE, ORDERS.O_SHIPPRIORITY, PARTSUPP.PS_SUPPKEY HAVING MIN(PARTSUPP.PS_AVAILQTY) > 7908 ORDER BY LINEITEM.L_COMMITDATE DESC, ORDERS.O_SHIPPRIORITY DESC;
