SELECT PARTSUPP.PS_PARTKEY, LINEITEM.L_QUANTITY, COUNT(PARTSUPP.PS_SUPPLYCOST), SUM(PARTSUPP.PS_SUPPLYCOST) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY GROUP BY PARTSUPP.PS_PARTKEY, LINEITEM.L_QUANTITY ORDER BY COUNT(PARTSUPP.PS_SUPPLYCOST) ASC, SUM(PARTSUPP.PS_SUPPLYCOST) ASC;
