SELECT LINEITEM.L_RECEIPTDATE, PART.P_MFGR, PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERDATE, MIN(ORDERS.O_ORDERSTATUS) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY LINEITEM.L_RECEIPTDATE, PART.P_MFGR, PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERDATE ORDER BY MIN(ORDERS.O_ORDERSTATUS) ASC;
