SELECT PARTSUPP.PS_SUPPLYCOST, PART.P_SIZE, NATION.N_NATIONKEY, REGION.R_NAME, SUPPLIER.S_COMMENT, MIN(PARTSUPP.PS_PARTKEY) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY PARTSUPP.PS_SUPPLYCOST, PART.P_SIZE, NATION.N_NATIONKEY, REGION.R_NAME, SUPPLIER.S_COMMENT ORDER BY MIN(PARTSUPP.PS_PARTKEY) ASC;
