SELECT NATION.N_NAME, PART.P_CONTAINER, SUPPLIER.S_NATIONKEY, PARTSUPP.PS_AVAILQTY, MAX(PARTSUPP.PS_SUPPLYCOST), AVG(SUPPLIER.S_SUPPKEY) FROM NATION, SUPPLIER, PARTSUPP, PART GROUP BY NATION.N_NAME, PART.P_CONTAINER, SUPPLIER.S_NATIONKEY, PARTSUPP.PS_AVAILQTY HAVING AVG(SUPPLIER.S_SUPPKEY) >= 7 ORDER BY PARTSUPP.PS_AVAILQTY ASC;
