SELECT NATION.N_NAME, LINEITEM.L_SHIPMODE, CUSTOMER.C_ADDRESS, ORDERS.O_ORDERKEY FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY ORDER BY CUSTOMER.C_ADDRESS DESC, NATION.N_NAME ASC, ORDERS.O_ORDERKEY DESC;
