SELECT CUSTOMER.C_ACCTBAL, LINEITEM.L_DISCOUNT, ORDERS.O_TOTALPRICE, SUM(ORDERS.O_ORDERKEY) FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY GROUP BY CUSTOMER.C_ACCTBAL, LINEITEM.L_DISCOUNT, ORDERS.O_TOTALPRICE HAVING SUM(ORDERS.O_ORDERKEY) < 1985;
